INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (19983691052, 'Who Was Laura Ingalls Wilder?', '', 'Paperback', 'http://any.bibliocommons.com/item/show/19983691052', '2013', '9780448467061', NULL, 'J B Wilder D', '"Born in 1867 in the "Big Woods" in Wisconsin, Laura experienced both the hardship and the adventure of living on the frontier. It wasn''t until after she was sixty that Laura Ingalls Wilder started chronicling those times, which resulted in nine Little House books, a hit TV series that ran for eight years, and her own permanent place as a heroine of the American West"--', '106 pages :,illustrations ;,20 cm.', 'Includes bibliographical references', 'by Patricia Brennan Demuth ; illustrated by Tim Foley', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780448467061', '2014-01-06 22:49:12.588753', '2014-01-16 21:41:12.661801');
