INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (16, 'March', 'Book One', 'Book', 'http://any.bibliocommons.com/item/show/19831523052907', '2013', '9781603093002', NULL, 'GRAPHIC B Lewis L', '', '1 volume, 121 pages :,black-and-white illustrations ;,25 cm.', '"March is a vivid first-hand account of John Lewis'' lifelong struggle for civil and human rights, meditating in the modern age on the distance traveled since the days of Jim Crow and segregation. Rooted in Lewis'' personal story, it also reflects on the highs and lows of the broader civil rights movement."--Back cover flap', 'John Lewis ; co-written by Andrew Aydin ; art by Nate Powell', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781603093002', '2014-01-14 22:33:18.394943', '2014-01-14 22:33:18.402497');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (19742859052, 'Brave Girl', 'Clara and the Shirtwaist Makers'' Strike of 1909', 'Book', 'http://any.bibliocommons.com/item/show/19742859052', '2013', '9780061804427', NULL, 'J B Lemlich M', 'The inspiring true story of Clara Lemlich, a young immigrant girl in early 20th-century New York who led the biggest strike of women workers in U.S. history.', '1 v. (unpaged) :,col. ill. ;,28 cm.', 'Includes bibliographical references', 'written by Michelle Markel ; pictures by Melissa Sweet', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780061804427', '2014-01-06 23:06:53.880516', '2014-01-16 19:41:37.146614');
