INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (19862826052, 'Who Is Barack Obama?', '', 'Paperback', 'http://any.bibliocommons.com/item/show/19862826052', '2012', '9780448453309', NULL, 'J B Obama E', 'As the world now knows, Barack Obama has made history as our first African-American president. With black-and-white illustrations throughout, this biography is perfect for primary graders looking for a longer, fuller life story than is found in the author''s bestselling beginning reader Barack Obama: United States President.', '106 pages :,illustrations, maps ;,20 cm.', 'Includes bibliographical references (p. 106)', 'by Roberta Edwards ; illustrated by John O''Brien', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780448453309', '2014-01-06 23:07:24.773394', '2014-01-16 21:40:38.120467');
