INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (19807814052, 'Stronger than Steel', 'Spider Silk DNA and the Quest for Better Bulletproof Vests, Sutures, and Parachute Rope', 'Book', 'http://any.bibliocommons.com/item/show/19807814052', '2013', '9780547681269', NULL, '595.44 H', '"In The Spider Silk Scientists, readers enter Randy Lewis'' lab where they come face to face with golden orb weaver spiders and genetically engineered goats, whose milk contains the proteins to spin spider silk--and to weave a nearly indestructible fiber. Learn how this amazing material might someday be used to repair or replace human ligaments and bones, improve body armor, strenghten parachute rope, and even tether an airplane to an aircraft carrier! Readers explore rapid advancements in the application of genetic medicine and their potential to save and improve lives while considering the crucial ethical concerns of genetic research. A timely addition to the acclaimed Scientists in the Field series. "--', '79 p. :,col. ill. ;,24 x 29 cm.', '"A junior library guild edition"--Jacket;Includes bibliographical references (p. 78) and index', 'Bridget Heos ; with photographs by Andy Comins', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780547681269', '2014-01-06 23:40:37.271283', '2014-01-16 22:05:14.335061');
