INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (19028530052, 'Drawing from Memory', '', 'Book', 'http://any.bibliocommons.com/item/show/19028530052', '2011', '9780545176866', NULL, 'J 741.64 Say S', '"Caldecott Medalist Allen Say presents a stunning graphic novel chronicling his journey as an artist during WWII, when he apprenticed under Noro Shinpei, Japan''s premier cartoonist.  DRAWING FROM MEMORY is Allen Say''s own story of his path to becoming the renowned artist he is today.  Shunned by his father, who didn''t understand his son''s artistic leanings, Allen was embraced by Noro Shinpei, Japan''s leading cartoonist and the man he came to love as his "spiritual father." As WWII raged, Allen was further inspired to consider questions of his own heritage and the motivations of those around him.  He worked hard in rigorous drawing classes, studied, trained--and ultimately came to understand who he really is.  Part memoir, part graphic novel, part narrative history, DRAWING FROM MEMORY presents a complex look at the real-life relationship between a mentor and his student.  With watercolor paintings, original cartoons, vintage photographs, and maps, Allen Say has created a book that will inspire the artist in all of us."--', '63 p. :,ill. ;,28 cm.', '', 'Allen Say', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780545176866', '2014-01-06 23:41:01.080447', '2014-01-16 22:09:26.494263');
