INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (109, 'Knucklehead', 'Tall Tales & Mostly True Stories about Growing up Scieszka', 'Book', 'http://nypl.bibliocommons.com/item/show/18047779052', '2008', '0670011061', NULL, 'J B Scieszka', 'How did Jon Scieszka get so funny? He grew up as one of six brothers with Catholic school, lots of comic books, lazy summers at the lake with time to kill, babysitting misadventures, TV shows, and jokes told at family dinner.', '106 p. :,ill., map ;,27 cm.', 'Includes index', 'Jon Scieszka', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0670011061', '2014-01-24 18:50:35.924586', '2014-01-24 18:50:35.935059');
