INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (112, 'Who Was Laura Ingalls Wilder?', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/19983691052', '2013', '9780448467061', NULL, 'J B Wilder D', '"Born in 1867 in the "Big Woods" in Wisconsin, Laura experienced both the hardship and the adventure of living on the frontier. It wasn''t until after she was sixty that Laura Ingalls Wilder started chronicling those times, which resulted in nine Little House books, a hit TV series that ran for eight years, and her own permanent place as a heroine of the American West"--', '106 pages :,illustrations ;,20 cm.', 'Includes bibliographical references', 'by Patricia Brennan Demuth ; illustrated by Tim Foley', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780448467061', '2014-01-24 18:51:09.000344', '2014-01-24 18:51:09.010505');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (110, 'Drawing from Memory', '', 'Book', 'http://nypl.bibliocommons.com/item/show/19028530052', '2011', '9780545176866', NULL, 'J 741.64 Say S', '"Caldecott Medalist Allen Say presents a stunning graphic novel chronicling his journey as an artist during WWII, when he apprenticed under Noro Shinpei, Japan''s premier cartoonist.  DRAWING FROM MEMORY is Allen Say''s own story of his path to becoming the renowned artist he is today.  Shunned by his father, who didn''t understand his son''s artistic leanings, Allen was embraced by Noro Shinpei, Japan''s leading cartoonist and the man he came to love as his "spiritual father." As WWII raged, Allen was further inspired to consider questions of his own heritage and the motivations of those around him.  He worked hard in rigorous drawing classes, studied, trained--and ultimately came to understand who he really is.  Part memoir, part graphic novel, part narrative history, DRAWING FROM MEMORY presents a complex look at the real-life relationship between a mentor and his student.  With watercolor paintings, original cartoons, vintage photographs, and maps, Allen Say has created a book that will inspire the artist in all of us."--', '63 p. :,ill. ;,28 cm.', '', 'Allen Say', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780545176866', '2014-01-24 18:50:36.759701', '2014-01-24 18:50:36.770326');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (113, 'Who Was Frida Kahlo?', '', 'Book', 'http://nypl.bibliocommons.com/item/show/19983690052', '2013', '9780448479385', NULL, 'J B kahlo F', '"You can always recognize a painting by Kahlo because she is in nearly all--with her black braided hair and colorful Mexican outfits. A brave woman who was an invalid most of her life, she transformed herself into a living work of art. As famous for her self-portraits and haunting imagery as she was for her marriage to another famous artist, Diego Rivera, this strong and courageous painter was inspired by the ancient culture and history of her beloved homeland, Mexico. Her paintings continue to inform and inspire popular culture around the world"--', '106 pages :,illustrations ;,20 cm.', 'Includes bibliographical references', 'by Sarah Fabiny ; illustrated by Jerry Hoare', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780448479385', '2014-01-24 18:51:09.78718', '2014-01-24 18:51:09.797809');
