INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (114, 'Who Is Steven Spielberg?', '', 'Book', 'http://nypl.bibliocommons.com/item/show/19983688052', '2013', '9780448479354', NULL, 'J B Spielberg S', '"While other kids played sports, Steven Spielberg was writing scripts and figuring out camera angles. He went from entertaining his Boy Scout troop with home movies to amazing audiences around the world with epic blockbusters. He has directed four of the most successful films of all time and has won two Academy Awards for Best Director. From Jaws to Lincoln, young readers and aspiring filmmakers will be fascinated by the life of this famous director"--', '105 pages :,illustrations ;,20 cm.', 'Includes bibliographical references', 'by Stephanie Spinner ; illustrated by Daniel Mather', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780448479354', '2014-01-24 18:51:10.563488', '2014-01-24 18:51:10.572273');
