INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (118, 'Who Was Walt Disney?', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/18802499052', '2009', '9780448450520', NULL, 'J B Disney S', 'Walt Disney always loved to entertain people. Often it got him into trouble. With black-and-white illustrations throughout, this biography reveals the man behind the magic.', '106 p. :,ill. ;,21 cm.', 'Includes bibliographical references (p. 104-105)', 'by Whitney Stewart ; illustrated by Nancy Harrison', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780448450520', '2014-01-24 18:51:13.776302', '2014-01-24 18:51:13.785065');
