INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (127, 'Who Was Claude Monet?', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/18138479052', '2009', '0448449854', NULL, 'J B Monet W', 'Claude Monet is considered one of the most influential artists of all time. He is a founder of the French Impressionist art movement, and today his paintings sell for millions of dollars. While Monet was alive, however, his work was often criticized and he struggled financially.  With over one hundred black-and-white illustrations, this book unveils a true portrait of the artist!', '105 p. :,ill., maps ;,19 cm.', 'Includes bibliographical references (p. 104-105)', 'by Ann Waldron ; illustrated by Stephen Marchesi', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0448449854', '2014-01-24 18:51:21.095391', '2014-01-24 18:51:21.104159');
