INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (129, 'Who Was Ernest Shackleton?', '', 'Book', 'http://nypl.bibliocommons.com/item/show/19983689052', '2013', '9780448479316', NULL, 'J B Shackleton B', '"As a boy he preferred reading sea stories to doing homework and, at age 16, became an apprentice seaman. Subsequently, Ernest Shackleton''s incredible journeys to the South Pole in the early 1900s made him one of the most famous explorers of modern times. His courage in the face of dangerous conditions and unforeseeable tragedies reveal the great leader that he was. His historic 1914 journey aboard the Endurance has all the drama of an action movie"--', '103 pages :,illustrations ;,20 cm.', 'Includes bibliographical references', 'James Buckley Jr. ; [illustrated by] Max Hergenrother', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780448479316', '2014-01-24 18:51:56.43548', '2014-01-24 18:51:56.489001');
