INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (310, 'The Giver', '', 'Book', 'http://nypl.bibliocommons.com/item/show/18055357052907', '2006', '0385732554', NULL, 'J FIC L', 'Given his lifetime assignment at the Ceremony of Twelve, Jonas becomes the receiver of memories shared by only one other in his community and discovers the terrible truth about the society in which he lives.', '179 , 11 p. :,ill. ;,21 cm.', 'Includes "A readers guide" (second pagination)', 'Lois Lowry', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0385732554', '2014-02-03 22:47:37.537504', '2014-02-03 22:47:37.548457');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (244, 'The Omnivore''s Dilemma', 'A Natural History of Four Meals', 'Book', 'http://nypl.bibliocommons.com/item/show/17685222052907', '2007', '9780143038580', NULL, '394.12 P', 'Today, buffeted by one food fad after another, America is suffering from what can only be described as a national eating disorder. Will it be fast food tonight, or something organic? Or perhaps something we grew ourselves? The question of what to have for dinner has confronted us since man discovered fire. But as Michael Pollan explains in this revolutionary book, how we answer it now, at the dawn of the twenty-first century may determine our survival as a species.--From publisher description.', '450 p. ;,22 cm.', 'Includes bibliographical references p. [417]-435) and index', 'Michael Pollan', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780143038580', '2014-02-03 22:44:20.54767', '2014-02-03 22:44:20.558684');
