INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (245, 'Unbroken', 'A World War II Story of Survival, Resilience, and Redemption', 'Book', 'http://nypl.bibliocommons.com/item/show/18580147052907', '2010', '9781400064168', NULL, 'B Zamperin H', 'On a May afternoon in 1943, an Army Air Forces bomber crashed into the Pacific Ocean and disappeared, leaving only a spray of debris and a slick of oil, gasoline, and blood. Then, on the ocean surface, a face appeared; it was Lt. Louis Zamperini. Captured by the Japanese and driven to the limits of endurance, Zamperini would answer desperation with ingenuity; suffering with hope, resolve, and humor. Zamperini had a troubled youth, yet honed his athletic skills and made it all the way to the 1934 Olympics in Berlin. However, what lay before him was a physical gauntlet unlike anything he had encountered before: thousands of miles of open ocean, a small raft, and no food or water.  He spent forty-seven days adrift in the ocean before being rescued by the Japanese Navy, and was held as a prisoner until the end of the war.', 'xviii, 473 p. :,ill., map ;,25 cm.', 'Includes bibliographical references (p. [407]-457) and index', 'Laura Hillenbrand', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781400064168', '2014-02-03 22:44:37.388788', '2014-02-03 22:44:37.600969');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (247, 'Dragonwings', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/18153019052907', '2001', '0064400859', NULL, 'J FIC Y', 'In the early twentieth century a young Chinese boy joins his father in San Francisco and helps him realize his dream of making a flying machine. "Moon shadow is eight years old when he sails from China to join his father, Windrider, in America. Windrider lives in San Francisco and makes his living doing laundry. Father and son have never met. But Moon Shadow grows to love and respect his father and to believe in his wonderful dream. And Windrider, with Moon Shadow''s help is willing to endure the mockery of the other Chinese, the poverty, the separation from his wife and country''even the great earthquake''to make his dream come true." -- P. [4] of cover.', '317 p. ;,20 cm.', '25th anniversary edition--Cover;Sequel: The Red Warrior;"Newbery Honor book, 1976"--Cover', 'by Laurence Yep', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0064400859', '2014-02-03 22:44:46.307072', '2014-02-03 22:44:46.318633');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (338, 'It Jes'' Happened', 'When Bill Traylor Started to Draw', 'Book', 'http://nypl.bibliocommons.com/item/show/19557375052', '2012', '9781600602603', NULL, 'J B Traylor T', '"A biography of twentieth-century African American folk artist Bill Traylor, a former slave who at the age of eighty-five began to draw pictures based on his memories and observations of rural and urban life in Alabama. Includes an afterword, author''s note, and sources"--Provided by publisher.', '1 v. (unpaged) :,col. ill. ;,23 x 28 cm.', 'Includes bibliographical references (T.p. verso)', 'by Don Tate ; illustrated by R. Gregory Christie', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781600602603', '2014-02-03 22:48:50.590186', '2014-02-03 22:48:50.599179');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (339, 'José!', 'Born to Dance : the Story of José Limón', 'Book', 'http://nypl.bibliocommons.com/item/show/17806998052', '2005', '0689865767', NULL, 'J B Limon R', 'Jose was born in Culiacan, Mexico. Though he and his family fled civil war in their homeland by escaping to the United States when Jose was just seven years old, he would never forget the sounds and movements of his birthplace. When he grew up he followed his heart to New York City and  discovered the world of dance. Jose went on to become one of the greatest modern dancers.', '1 v. (unpaged) :,col. ill. ;,29 cm.', '"A Paula Wiseman book.";Includes bibliographical references', 'Susanna Reich ; illustrated by Raúl Colón', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0689865767', '2014-02-03 22:48:51.398373', '2014-02-03 22:48:51.410261');
