INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (276, 'Balloons over Broadway', 'The True Story of the Puppeteer of Macy''s Parade', 'Book', 'http://nypl.bibliocommons.com/item/show/19115508052907', '2011', '9780547199450', NULL, 'J B Sarg S', 'Award-winning artist Sweet tells the story of the puppeteer Tony Sarg, capturing his genius, his dedication, his zest for play, and his long-lasting gift to America--the inspired helium balloons that would become the trademark of Macy''s Thanksgiving Day Parade.', '1 v. (unpaged) :,col. ill. ;,24 x 29 cm.', 'Includes bibliographical references', 'written and illustrated by Melissa Sweet', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780547199450', '2014-02-03 22:46:11.538901', '2014-02-03 22:46:11.550251');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (260, 'To Dare Mighty Things', 'The Life of Theodore Roosevelt', 'Book', 'http://nypl.bibliocommons.com/item/show/19966820052907', '2013', '9781423124887', NULL, 'J B Roosevelt R', '"President Theodore Roosevelt is known as "the man with a plan," the "rough rider." His figure stands tall in American history; his legacy stretching him to larger-than-life proportions. But before his rise to fame, he was just "Teedie," a boy with ambitious dreams to change the world, and the conviction to see his stupendous imaginings brought to fruition. As an American president, he left an impressive mark upon his country. He promised a "square deal" to all citizens, he tamed big businesses, and protected the nation''s wildlife and natural beauty. His fearless leadership assured that he would always be remembered, and his robust spirit now dares others to do mighty things. In her moving picture book portrait, award-winning author Doreen Rappaport uses her well-honed approach of personal quotes and vivid prose to spin together the tale of a sickly boy who became a monumental man. Coupled with C.F. Payne''s dramatic artwork, the story of President Teddy, touchstone of American history, is brought to life"--', '1 volume (unpaged) :,color illustrations ;,29 cm', '', 'by Doreen Rappaport ; illustrated by C. F. Payne', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781423124887', '2014-02-03 22:45:35.387344', '2014-02-03 22:45:35.395885');
