INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (279, 'All the Way to America', 'The Story of A Big Italian Family and A Little Shovel', 'Book', 'http://nypl.bibliocommons.com/item/show/18759794052907', '2011', '9780375866425', NULL, 'J 813.54 Y', 'This is the story of four generations of an Italian American family. It begins with an immigrant who came through Ellis Island with big dreams, a small shovel, and his parents'' good advice: "Work hard, but remember to enjoy life, and never forget your family."', '1 v. (unpaged) :,col. ill. ;,22 x 27 cm.', '', 'Dan Yaccarino', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780375866425', '2014-02-03 22:46:16.461949', '2014-02-03 22:46:16.473005');
