INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (284, 'Miles to Go for Freedom', 'Segregation & Civil Rights in the Jim Crow Years', 'Book', 'http://nypl.bibliocommons.com/item/show/19473198052907', '2012', '1419700200', NULL, '305.896 O', 'Told through unforgettable first-person accounts, photographs, and other primary sources, and published in association with the Library of Congress, this book is an overview of racial segregation and early civil rights efforts in the United States from the 1890s to 1954, a period known as the Jim Crow years. Photos & illustrations.', 'ix, 118 p. :,ill. (some col.), map ;,27 cm.', 'Includes bibliographical references (p. 113-) and index;"Published in association with the Library of Congress."', 'Linda Barrett Osborne', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=1419700200', '2014-02-03 22:46:32.011393', '2014-02-03 22:46:32.022716');
