INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (280, 'When the Beat Was Born', 'DJ Kool Herc and the Creation of Hip Hop', 'Book', 'http://nypl.bibliocommons.com/item/show/19885684052907', '2013', '9781596435407', NULL, 'J B Kool DJ Her H', 'Before there was hip hop, there was DJ Kool Herc. On a hot day at the end of summer in 1973 Cindy Campbell threw a back-to-school party at a park in the South Bronx. Her brother, Clive Campbell, spun the records. He had a new way of playing the music to make the breaks the musical interludes between verses longer for dancing. He called himself DJ Kool Herc and this is When the Beat Was Born. From his childhood in Jamaica to his youth in the Bronx, Laban Carrick Hill''s book tells how Kool Herc came to be a DJ, how kids in gangs stopped fighting in order to breakdance, and how the music he invented went on to define a culture and transform the world.', '1 volume (unpaged) :,color illustrations ;,26 cm.', 'Includes bibliographical references and filmography', 'Laban Carrick Hill ; illustrated by Theodore Taylor III', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781596435407', '2014-02-03 22:46:19.063602', '2014-02-03 22:46:19.074998');
