INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (360, 'Who Was Frida Kahlo?', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/19983690052907', '2013', '9780448479385', NULL, 'J B kahlo F', '"You can always recognize a painting by Kahlo because she is in nearly all--with her black braided hair and colorful Mexican outfits. A brave woman who was an invalid most of her life, she transformed herself into a living work of art. As famous for her self-portraits and haunting imagery as she was for her marriage to another famous artist, Diego Rivera, this strong and courageous painter was inspired by the ancient culture and history of her beloved homeland, Mexico. Her paintings continue to inform and inspire popular culture around the world"--', '106 pages :,illustrations ;,20 cm.', 'Includes bibliographical references', 'by Sarah Fabiny ; illustrated by Jerry Hoare', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780448479385', '2014-02-03 22:49:25.538252', '2014-02-03 22:49:25.548192');
