INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (490, 'The Boy Who Loved Math', 'The Improbable Life of Paul Erdős', 'Book', 'http://nypl.bibliocommons.com/item/show/19886313052907', '2013', '9781596433076', NULL, 'J B Erdos H', 'Growing up in Hungary during WWI, Erdos tried school but chafed at the rules and convinced his mother that he should study at home. He was fascinated by numbers from an early age, and by the time he was 20, he was known as The Magician from Budapest. Unable to do common tasks such as cooking, laundry, or driving, he spent his adult life flying around the world, staying with other mathematicians, and working collaboratively on challenging math problems.', '37 pages :,color illustrations ;,26 cm', '', 'by Deborah Heiligman ; pictures by LeUyen Pham', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781596433076', '2014-02-03 22:57:10.573391', '2014-02-03 22:57:10.585019');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (489, 'Wild Fibonacci', 'Nature''s Secret Code Revealed', 'Paperback', 'http://nypl.bibliocommons.com/item/show/18541628052907', '2010', '9781582463247', NULL, 'J 512.72 H', '1, 1, 2, 3, 5, 8, 13, 21, 34... Look carefully. Do you see the pattern? Each number above is the sum of the two numbers before it. Though most of us are unfamiliar with it, this numerical series, called the Fibonacci sequence, is part of a code that can be found everywhere in nature. Count the petals on a flower or the peas in a peapod. The numbers are all part of the Fibonacci sequence. In Wild Fibonacci, readers will discover this mysterious code in a special shape called an equiangular spiral. Why so special? It mysteriously appears in the natural world: a sundial shell curves to fit the spiral. So does a parrot''s beak...a hawk''s talon...a ram''s horn...even our own human teeth! Joy Hulme provides a clear and accessible introduction to the Fibonacci sequence and its presence in the animal world.', '1 v. (unpaged) :,col. ill. ;,27 cm.', '', 'by Joy N. Hulme ; illustrations by Carol Schwartz', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781582463247', '2014-02-03 22:57:08.70113', '2014-02-03 22:57:08.712481');
