INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (614, 'War Comes to Willy Freeman', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/19798882052907', '2005', '9780440495048', NULL, 'J FIC C', 'Disguised as a boy, Willy begins a long search for her mother after she witnesses her father''s death at the hands of the British Redcoats; and as a free black female, she knows she will face many dangers.', '178 pages. ;,20 cm.', '"February 1987"--T.p. verso;"Cover art © 2005 by Gordon Crabb.";Reprint. Originally published in 1983', 'James Lincoln Collier and Christopher Collier', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780440495048', '2014-02-03 23:04:54.013064', '2014-02-03 23:04:54.025029');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (613, 'Persepolis', '', 'Book', 'http://nypl.bibliocommons.com/item/show/17877661052907', '2003', '0375422307', NULL, 'B Satrapi', 'An intelligent and outspoken only child, Satrapi--the daughter of radical Marxists and the great-granddaughter of Iran''s last emperor--bears witness to a childhood uniquely entwined with the history of her country. Originally published to wide critical acclaim in France, where it elicited comparisons to Art Spiegelman''s Maus, Persepolis is Marjane Satrapi''s wise, funny, and heartbreaking memoir of growing up in Iran during the Islamic Revolution. In powerful black-and-white comic strip images, Satrapi tells the story of her life in Tehran from ages six to fourteen, years that saw the overthrow of the Shah''s regime, the triumph of the Islamic Revolution, and the devastating effects of war with Iraq. The intelligent and outspoken only child of committed Marxists and the great-granddaughter of one of Iran''s last emperors, Marjane bears witness to a childhood uniquely entwined with the history of her country. Persepolis paints an unforgettable portrait of daily life in Iran: of the bewildering contradictions between home life and public life and of the enormous toll repressive regimes exact on the individual spirit. Marjane''s child''s-eye-view of dethroned emperors, state-sanctioned whippings, and heroes of the revolution allows us to learn as she does the history of this fascinating country and of her own extraordinary family. Intensely personal, profoundly political, and wholly original, Persepolis is at once a story of growing up and a stunning reminder of the human cost of war and political repression. It shows how we carry on, through laughter and tears, in the face of absurdity. And, finally, it introduces us to an irresistible little girl with whom we cannot help but fall in love.', '153 p. :,ill. ;,24 cm.', 'Translated from the French', 'Marjane Satrapi', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0375422307', '2014-02-03 23:04:49.420653', '2014-02-03 23:04:49.434658');
