INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (775, 'The Crucible', 'A Play in Four Acts', 'Book', 'http://nypl.bibliocommons.com/item/show/17788773052907', '2003', '0142437336', NULL, '812 Miller', '"I believe that the reader will discover here the essential nature of one of the strangest and most awful chapters in human history," Arthur Miller wrote in an introduction to The Crucible, his classic play about the witch-hunts and trials in seventeenth-century Salem, Massachusetts. Based on historical people and real events, Miller''s drama is a searing portrait of a community engulfed by hysteria. In the rigid theocracy of Salem, rumors that women are practicing witchcraft galvanize the town''s most basic fears and suspicions; and when a young girl accuses Elizabeth Proctor of being a witch, self-righteous church leaders and townspeople insist that Elizabeth be brought to trial. The ruthlessness of the prosecutors and the eagerness of neighbor to testify against neighbor brilliantly illuminate the destructive power of socially sanctioned violence. Written in 1953, The Crucible is a mirror Miller uses to reflect the anti-communist hysteria inspired by Senator Joseph McCarthy''s witch-hunts in the United States. Within the text itself, Miller contemplates the parallels, writing: "Political opposition...is given an inhumane overlay, which then justifies the abrogation of all normally applied customs of civilized behavior. A political policy is equated with moral right, and opposition to it meets with diabolical malevolence."', 'xxv, 143 p. ;,20 cm.', '', 'Arthur Miller ; with an introduction by Christopher Bigsby', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0142437336', '2014-02-03 23:14:13.570602', '2014-02-03 23:14:13.582313');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (784, 'Like Water for Chocolate', 'A Novel in Monthly Installments, with Recipes, Romances, and Home Remedies', 'Book', 'http://nypl.bibliocommons.com/item/show/17831340052907', '1995', '038542017X', NULL, 'FIC E', 'With more than two million copies in print, this beloved novel has become a treasured part of America''s literary memory. Now, for the first time, this "tall-tale, fairy-tale, soap opera romance, Mexican cookbook, and home-remedy handbook all rolled into one" (San Francisco Chronicle) is available in trade paper with the original art from the hardcover.', '245 p. ;,21 cm.', '', 'Laura Esquivel ; translated by Carol Christensen and Thomas Christensen', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=038542017X', '2014-02-03 23:23:35.052', '2014-02-03 23:23:35.063569');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (781, 'Breaking and Entering', 'A Novel', 'Book', 'http://nypl.bibliocommons.com/item/show/19470707052907', '2012', '9781935536123', NULL, 'FIC P', 'It''s 1995, and Louise Shapiro has left California to start a new life in rural Michigan with her husband, Richard, and their daughter, Molly. Within weeks, an explosion destroys the Murrah Federal Building in Oklahoma City, leaving innocent civilians dead and maimed. As the accusations and conspiracy theories proliferate- about Arabs, Jews, Christians, the FBI, the government- Louise discovers startling secrets about her neighbors, her colleagues, and even her own husband. Can she live in a town where most of the residents are members of the Michigan Militia? Can she work in a school where her students consider homosexuality the work of Satan? Whom can she trust? Em, the local Wiccan? The charismatic Unitarian minister Ames Wye? Her troubled student, Parker Rosenkrantz?--From inside front cover.', '375 p. ;,23 cm.', '', 'Eileen Pollack', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781935536123', '2014-02-03 23:23:23.835545', '2014-02-03 23:23:23.846512');
