INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (729, 'The Odyssey', '', 'Book', 'http://nypl.bibliocommons.com/item/show/18280486052907', '1997', '0140268863', NULL, '883 Homer', 'In the myths and legends that are magnificently retold here, Fagles has captured the energy and poetry of Homer''s original in a bold, contemporary idiom, and given readers an "Odyssey" to read aloud, to savor, and to treasure for its sheer lyrical mastery.', '541 p. ;,22 cm.', '"This [pbk.] edition contains minor revisions of the text"--P. 496;Includes bibliographical references', 'Homer ; translated by Robert Fagles ; introduction and notes by Bernard Knox', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0140268863', '2014-02-03 23:10:51.77481', '2014-02-03 23:10:51.783402');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (746, 'Everyday Life in Ancient China', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/19665975052907', '2012', '9781448862184', NULL, 'J 931 H', 'Life in ancient China was vastly different from life in the heavily-populated, industrialized China of today; most of the people in ancient China farmed and worked the land for food. Explore the daily activities of one of those farmers while reviewing the government, education, technology, and culture of this rich civilization. Told in graphic novel format.', '24 p. :,col. ill. ;,26 cm.', 'Includes index', 'by Kirsten Campbell Holm', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781448862184', '2014-02-03 23:12:14.740258', '2014-02-12 16:47:40.65671');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (728, 'El Bronx Remembered', 'A Novella and Stories', 'Paperback', 'http://nypl.bibliocommons.com/item/show/17364959052907', '1993', '0064471004', NULL, 'FIC M', 'In the South Bronx--or El Bronx, as it''s known to the people who live there--anything can happen. A migrant from Puerto Rico can become somebody on the mainland, pursue the American Dream--and maybe even make it come true. Harsh, happy, scary, sad, and funny, here are stories of growing up in the South Bronx during its heyday, from 1946 to 1956.', '263 p. ;,18 cm.', 'Originally published in 1975', 'by Nicholasa Mohr', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0064471004', '2014-02-03 23:10:48.30515', '2014-02-03 23:10:48.316048');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (742, 'Hussein Chalayan', '', 'Book', 'http://nypl.bibliocommons.com/item/show/19275193052907', '2011', '9780847833863', NULL, '746.9209 Chalayan', '"The comprehensive book on the visionary Hussein Chalayan, one of the most innovative, experimental, and conceptual fashion designers working today. Internationally acclaimed, Hussein Chalayan is known for his inventive use of materials and integration of new technology into his designs. He is also celebrated for putting the creative process itself on view. Some of his best-known designs include a paper dress that can be folded into an envelope and airmailed, armchair covers that transform into dresses, and a coffee table that reveals itself to be a wooden skirt. Original and groundbreaking, his designs are also pretty and modern, and this book explores that continuum. Featuring Chalayan''s complete body of fashion and creative work--including his installations, videos, and photographs"-- Publisher''s web site.', '276 p. : ill. (some col.) ;,31 cm.', '', 'texts by Judith Clark, Susannah Frankel, Pamela Golbin [et al.] ; drawings by Hussein Chalayan ; edited by Robert Violette', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780847833863', '2014-02-03 23:11:55.772988', '2014-02-03 23:11:55.784552');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (743, '1984', 'A Novel', 'Book', 'http://nypl.bibliocommons.com/item/show/18708792052907', '1977', '0451524934', NULL, 'CLASSICS FIC O', 'Portrays life in a future time when a totalitarian government watches over all citizens and directs all activities.', '328 p. ;,17 cm.', '"1984 has come and gone, but George Orwell''s prophetic, nightmarish vision in 1949 ..."--Back paperback cover;Includes bibliographical references (p. [327]-328)', 'by George Orwell ; with an afterword by Erich Fromm', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0451524934', '2014-02-03 23:11:59.019999', '2014-02-03 23:11:59.028671');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (741, 'A Raisin in the Sun', '', 'Book', 'http://nypl.bibliocommons.com/item/show/14077669052907', '1994', '0679755330', NULL, '812 Hansberry', 'When it was first produced in 1959, A Raisin in the Sun was awarded the New York Drama Critics Circle Award for that season and hailed as a watershed in American drama. A pioneering work by an African-American playwright, the play was a radically new representation of black life.  "A play that changed American theater forever."', '151 p. ;,18 cm.', '"Originally published in 1958."', 'Lorraine Hansberry ; with an introduction by Robert Nemiroff', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0679755330', '2014-02-03 23:11:47.714916', '2014-02-03 23:11:47.725999');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (744, 'Monkey King', 'Birth of the Stone Monkey', 'Paperback', 'http://nypl.bibliocommons.com/item/show/19794702052907', '2011', '9788994208459', NULL, 'J GRAPHIC GN FIC C', 'Follows the adventures of Sun Wu Kong, born from a stone on Spring Mountain and given the title of the Handsome Monkey King, becomes extremely powerful while seeking the secret of eternal life, and his hubris grows until he runs afoul of the gods, who send an army to Spring Mountain to subdue him.', '173 p. :,chiefly col. ill. ;,19 cm.', 'Original story, "The journey to the West" by Wu, Cheng En;"Monkey king ... has been recognized by the Ministry of Culture as one of the ''representative comic works'' of China"--Cover verso;"Authorized by Tianjin Shenjie comics, China. Translation supported by the Confucius Institute"--Cover verso', 'created by Wei Dong Chen ; illustrated by Chao Peng', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9788994208459', '2014-02-03 23:12:11.25484', '2014-02-03 23:12:11.368293');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (745, 'Ancient China', 'An Interactive History Adventure', 'Paperback', 'http://nypl.bibliocommons.com/item/show/19665948052907', '2013', '9781429647786', NULL, 'J 931 C', '"Describes the life and times of the era known as ancient China. The readers'' choices reveal the historical details of life during the Qin dynasty, life under Empress Wu Zetian, and life as a trader on the Silk Road"--Provided by publisher.', '112 p. :,ill. ;,20 cm.', 'Includes bibliographical references and index', 'by Terry Collins', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9781429647786', '2014-02-03 23:12:13.04743', '2014-02-03 23:12:13.055968');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (730, 'When I Was Puerto Rican', '[a Memoir]', 'Book', 'http://nypl.bibliocommons.com/item/show/17108979052907', '2006', '0306814528', NULL, 'B Santiago S', '[The author''s] story begins in rural Puerto Rico, where her warring parents and seven siblings led a life of uproar, but one full of love and tenderness as well. Growing up, Esmeralda learned the proper way to eat a guava, the sound of the tree frogs in the mango groves at night, the taste of the delectable sausage called morcilla, and the formula for ushering a dead baby''s soul to heaven. But just when Esmeralda seemed to have learned everything, she was taken to New York City, where the rules - and the language - were bewilderingly different. How Esmeralda overcame adversity, won acceptance to New York City''s High School of Performing Arts, and then went on to Harvard, where she graduated with highest honors, is a record of a tremendous journey by a truly remarkable woman.-BooksInPrint.', '278 p. ;,21 cm.', 'Continued by: Almost a woman;Originally published: New York : Addison-Wesley, c1993;"A Merloyd Lawrence book.";Includes reader''s guide (p. [275]-278)', 'Esmeralda Santiago', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0306814528', '2014-02-03 23:10:55.37874', '2014-02-03 23:10:55.390312');
