INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (801, 'Tuesdays with Morrie', 'An Old Man, A Young Man, and Life''s Greatest Lesson', 'Book', 'http://nypl.bibliocommons.com/item/show/18545463052907', '2007', '9780767905923', NULL, 'B Schwartz A', 'Maybe it was a grandparent, or a teacher, or a colleague. Someone older, patient and wise, who understood you when you were young and searching, helped you see the world as a more profound place, gave you sound advice to help you make your way through it. For Mitch Albom, that person was Morrie Schwartz, his college professor from nearly twenty years ago. Maybe, like Mitch, you lost track of this mentor as you made your way, and the insights faded, and the world seemed colder. Wouldn''t you like to see that person again, ask the bigger questions that still haunt you, receive wisdom for your busy life today the way you once did when you were younger? Mitch Albom had that second chance. He rediscovered Morrie in the last months of the older man''s life. Knowing he was dying, Morrie visited with Mitch in his study every Tuesday, just as they used to back in college. Their rekindled relationship turned into one final "class": lessons in how to live. Tuesdays with Morrie is a magical chronicle of their time together, through which Mitch shares Morrie''s lasting gift with the world.', '199 p. ;,19 cm.', '', 'Mitch Albom', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780767905923', '2014-02-03 23:25:17.341136', '2014-02-03 23:25:17.352785');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (805, 'Savvy', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/18309939052907', '2010', '9780142414330', NULL, 'J FIC L', 'Recounts the adventures of Mibs Beaumont, whose thirteenth birthday has revealed her "savvy"--a magical power unique to each member of her family--just as her father is injured in a terrible accident.', '342, 15 p. :,[1] ill. ;,20 cm.', 'Includes preview of Scumble, "the next book about the Beaumont family";"First published in the United States of America by Dial Books for Young Readers ... 2008"--T.p. verso', 'by Ingrid Law', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780142414330', '2014-02-03 23:25:31.087289', '2014-02-03 23:25:31.099163');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (803, 'The Wave : [a Novel]', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/17364972052907', '2005', '0440993717', NULL, 'FIC S', 'A high school experiment to test social interaction backfires when an elitist group is formed. What starts as an innocent experiment brings terror to a high school. The Wave is based on a true incident that occured in a high school history class in Palo Alto, California, in 1969. The powerful forces of group pressure that pervaded many historic movements such as Nazism are recreated in the classroom when history teacher Burt Ross introduces a "new" system to his students. And before long The Wave, with its rules of "strength through discipline, community, and action," sweeps from the classroom through the entire school. And as most of the students join the movement, Laurie Saunders and David Collins recognize the frightening momentum of The Wave and realize they must stop it before it''s too late.', '138 p. ;,18 cm.', 'Subtitle from cover;"This work is a novelization of a teleplay by Johnny Dawkins based on a short story by Ron Jones."--T.p. verso;"The classroom experiment that went to0 far."--Cover', 'Todd Strasser', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0440993717', '2014-02-03 23:25:23.926963', '2014-02-03 23:25:23.938468');
