INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (808, 'It''s Not about the Bike', 'My Journey Back to Life', 'Paperback', 'http://nypl.bibliocommons.com/item/show/17221489052907', '2001', '0425179613', NULL, '796.6209 A', 'This is the story of one man''s journey through triumph, tragedy, transformation, and transcendence. It is the story of Lance Armstrong, the six-time winner of the Tour de France, and his fight against cancer. People magazine called it "inspiring." The New York Times called it "fascinating." But perhaps the Cincinnati Enquirer said it best: "It''s not about the bike, or about the sport. It''s about the soul."', '289 p., [16] p. of plates :,ill. ;,23 cm.', '"Now includes new chapter on Tour de France 2000 & Olympics"--Cover', 'Lance Armstrong with Sally Jenkins', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0425179613', '2014-02-03 23:26:16.499324', '2014-02-03 23:26:16.510713');
INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (809, 'Wonderstruck', 'A Novel in Words and Pictures', 'Book', 'http://nypl.bibliocommons.com/item/show/19077009052907', '2011', '9780545027892', NULL, 'J FIC S', 'Having lost his mother and his hearing in a short time, twelve-year-old Ben leaves his Minnesota home in 1977 to seek the father he never knew in New York City, and meets there Rose, who is also longing for something missing from her life. Ben''s story is told in words; Rose''s in pictures.', '637 p. :,ill. ;,22 cm.', 'Includes bibliographical references (p. 635-637)', 'Brian Selznick', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780545027892', '2014-02-03 23:26:26.65205', '2014-02-03 23:26:26.66168');
