INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (840, 'Crime and Punishment', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/18055549052907', '2006', '0451530063', NULL, 'CLASSICS  FIC D', 'Determined to overreach his humanity and assert his untrammelled individual will, Raskolnikov, an impoverished student living in the St. Petersburg of the Tsars, commits an act of murder and theft and sets into motion a story which, for its excrutiating suspense, its atmospheric vividness, and its profundity of characterization and vision, is almost unequaled in the literatures of the world. The best known of Dostoevsky''s masterpieces, Crime and Punishment can bear any amount of rereading without losing a drop of its power over our imagination.', 'xix, 536 p. ;,18 cm.', 'Includes bibliographical references (p. [535]-536)', 'Fyodor Dostoyevsky ; translated by Sidney Monas ; with an introduction by Leonard J. Stanton and James D. Hardy, Jr. ; and a new afterword by Robin Feuer Miller', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0451530063', '2014-02-03 23:28:19.821667', '2014-02-03 23:28:19.830424');
