INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (1080, 'Daniel Deronda', '', 'Book', 'http://nypl.bibliocommons.com/item/show/18708762052907', '1995', '0140434275', NULL, 'CLASSICS FIC E', 'Deronda, a high-minded young man searching for his path in life, finds himself drawn by a series of dramatic encounters into two contrasting worlds: the English country-house life of Gwendolen Harleth, a high-spirited beauty trapped in an oppressive marriage, and the very different lives of a poor Jewish girl, Mirah, and her family. As Deronda uncovers the long-hidden secret of his own parentage, Eliot''s moving and suspenseful narrative opens up a world of Jewish experience previously unknown to the Victorian novel.', 'xl, 849 p. ;,20 cm.', 'Includes bibliographical references (p. xxxvi-[xxxix])', 'George Eliot ; edited with introduction and notes by Terence Cave', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0140434275', '2014-02-12 17:17:49.984438', '2014-02-12 17:17:49.994258');
