INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (1083, 'To Kill A Mockingbird', '', 'Book', 'http://nypl.bibliocommons.com/item/show/17530329052907', '2006', '9780061120084', NULL, 'FIC L', 'A gripping, heart-wrenching, and wholly remarkable tale of coming-of-age in a South poisoned by virulent prejudice, it views a world of great beauty and savage inequities through the eyes of a young girl, Scout, as her father, Atticus Finch -- a crusading local lawyer-- risks everything to defend a black man unjustly accused of a terrible crime.', '323 p. ;,21 cm.', 'Originally published: Philadelphia: Lippincott; London: Heinemann, 1960', 'Harper Lee', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=9780061120084', '2014-02-12 17:24:19.273414', '2014-02-12 17:24:19.283817');
