INSERT INTO books (id, title, sub_title, format, details_url, publication_date, isbn, primary_language, call_number, description, physical_description, notes, statement_of_responsibility, cover_uri, created_at, updated_at) VALUES (1088, 'The Human Comedy', '', 'Paperback', 'http://nypl.bibliocommons.com/item/show/18096312052907', '1971', '0440339332', NULL, 'CLASSICS FIC S', 'The place is Ithaca, in California''s San Joaquin Valley. The time is World War II. The family is the Macauley''s -- a mother, sister, and three brothers whose struggles and dreams reflect those of America''s second-generation immigrants.. In particular, fourteen-year-old Homer, determined to become one of the fastest telegraph messengers in the West, finds himself caught between reality and illusion as delivering his messages of wartime death, love, and money brings him face-to-face with human emotion at its most naked and raw. Gentle, poignant and richly autobiographical, this delightful novel shows us the boy becoming the man in a world that even in the midst of war, appears sweeter, safer and more livable than out own.', '192 p. :,ill. ;,18 cm.', 'A Laurel edition', 'William Saroyan', 'http://contentcafe2.btol.com/ContentCafe/Jacket.aspx?&userID=NYPL49807&password=CC68707&content=M&Return=1&Type=L&Value=0440339332', '2014-02-12 17:27:51.91301', '2014-02-12 17:27:51.923202');
